mapper.select:

select  from kg_user


    gmt_create = #{record.gmtCreate,jdbcType=TIMESTAMP}


    and gmt_modified = #{record.gmtModified,jdbcType=TIMESTAMP}


    and user_no = #{record.userNo,jdbcType=VARCHAR}


    and token = #{record.token,jdbcType=VARCHAR}


    and last_token = #{record.lastToken,jdbcType=VARCHAR}


    and salt = #{record.salt,jdbcType=VARCHAR}


    and gmt_last_token_disable = #{record.gmtLastTokenDisable,jdbcType=TIMESTAMP}


    and dw_access_id = #{record.dwAccessId,jdbcType=VARCHAR}


    and dw_access_key = #{record.dwAccessKey,jdbcType=VARCHAR}


    and real_name = #{record.realName,jdbcType=VARCHAR}


    and nick_name = #{record.nickName,jdbcType=VARCHAR}


    and domain_account = #{record.domainAccount,jdbcType=VARCHAR}


order by id desc limit #{start}, #{size}


select count(*) from kg_user


    gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


    and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


    and user_no = #{userNo,jdbcType=VARCHAR}


    and token = #{token,jdbcType=VARCHAR}


    and last_token = #{lastToken,jdbcType=VARCHAR}


    and salt = #{salt,jdbcType=VARCHAR}


    and gmt_last_token_disable = #{gmtLastTokenDisable,jdbcType=TIMESTAMP}


    and dw_access_id = #{dwAccessId,jdbcType=VARCHAR}


    and dw_access_key = #{dwAccessKey,jdbcType=VARCHAR}


    and real_name = #{realName,jdbcType=VARCHAR}


    and nick_name = #{nickName,jdbcType=VARCHAR}


    and domain_account = #{domainAccount,jdbcType=VARCHAR}
